import React, { Component } from 'react';
//var ReactDOM = require('react-dom');
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class App extends Component {

  constructor(props) {
		super(props);

		// set initial state
		this.state = {
			isModalOpen: false,
      isInnerModalOpen: false,
      response: [],
      users: [],
      user: [],
      startDate: new Date('m/d/y')
		};
		// bind functions
		this.closeModal = this.closeModal.bind(this);
		this.openModal = this.openModal.bind(this);
  }

   handleChange = date => {
    this.setState({
      startDate: date
    });
  };


	// close modal (set isModalOpen, true)
	closeModal() {
		this.setState({
			isModalOpen: false
		});
	}

	// open modal (set isModalOpen, false)
	openModal(userData) {
   //alert(userData.activity_periods[0].start_time)
		this.setState({
      isModalOpen: true,
      user:userData
    });
  }

  componentDidMount() {
    fetch('http://localhost:9000/api/users/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ response: data })
      this.setState({ users: data.members })
      console.log('full response===>')
      console.log(this.state.response)
      console.log('abstract users data only===>')
      console.log(this.state.users)
    })
    .catch(console.log)
  }
  render() {
    return (
      <div className="container">
      <div className="col-xs-12">
      <h1 style={{textAlign: "center", color: "green", marginTop: 40}}>All Users</h1>
      {this.state.users.map((user) => (
        <div className="card" key={user.id} onClick={() => this.openModal(user)}>
          <div className="card-body">
            <h5 className="card-title">Name: {user.real_name}</h5>
            <h6 className="card-subtitle mb-2 text-muted">Id: {user.id}</h6>
          </div>
        </div>
      ))}

      <Modal
        isModalOpen={this.state.isModalOpen}
        closeModal={this.closeModal}
        style={modalStyle}
      >

        {/* <button
          style={{
            ...mainStyle.button,
            margin: 0,
            width: "auto",
            marginTop: 10,
            float: "left"
          }}
          onClick={this.closeModal}
        >
          View Activity
        </button> */}

        <button
          style={{
            ...mainStyle.button,
            margin: 0,
            width: "auto",
            marginTop: 10,
            float: "right"
          }}
          onClick={this.closeModal}
        >
          Close 
        </button>

        <p>Select Date {} </p>
        <DatePicker
        selected={this.state.startDate}
        onChange={this.handleChange}
      />
        <p>selected Date: </p>{this.state.startDate.toString()}
        <p>{this.state.user.id}</p>

      </Modal>

      </div>
     </div>
    );
  }
}

class Modal extends React.Component {
	// static propTypes = {
	// 	isModalOpen: React.PropTypes.bool.isRequired,
	// 	closeModal: React.PropTypes.func.isRequired,
	// 	style: React.PropTypes.shape({
	// 		modal: React.PropTypes.object,
	// 		overlay: React.PropTypes.object
	// 	})
	// };

	constructor(props) {
		super(props);

		this.outerStyle = {
			position: "fixed",
			top: 0,
			left: 0,
			width: "100%",
			height: "100%",
			overflow: "auto",
			zIndex: 1
		};

		// default style
		this.style = {
			modal: {
				position: "relative",
				width: 500,
				padding: 20,
				boxSizing: "border-box",
				backgroundColor: "#fff",
				margin: "40px auto",
				borderRadius: 3,
				zIndex: 2,
				textAlign: "left",
				boxShadow: "0 20px 30px rgba(0, 0, 0, 0.2)",
				...this.props.style.modal
			},
			overlay: {
				position: "fixed",
				top: 0,
				bottom: 0,
				left: 0,
				right: 0,
				width: "100%",
				height: "100%",
				backgroundColor: "rgba(0,0,0,0.5)",
				...this.props.style.overlay
			}
		};
	}

	// render modal
	render() {
		return (
			<div
				style={{
					...this.outerStyle,
					display: this.props.isModalOpen ? "block" : "none"
				}}
			>
				<div style={this.style.overlay} onClick={this.props.closeModal} />
				<div onClick={this.props.closeModal} />
				<div style={this.style.modal}>{this.props.children}</div>
			</div>
		);
	}
}

// overwrite style
const modalStyle = {
	overlay: {
		backgroundColor: "rgba(0, 0, 0,0.5)"
	}
};

const mainStyle = {
	app: {
		margin: "120px 0"
	},
	button: {
		backgroundColor: "#408cec",
		border: 0,
		padding: "12px 20px",
		color: "#fff",
		margin: "0 auto",
		width: 150,
		display: "block",
		borderRadius: 3
	}
};

//ReactDOM.render(<App />, document.getElementById("app"));
export default App;
