import React, { Component } from 'react';
import Modal, {closeStyle} from 'simple-react-modal'

class App extends Component {
  constructor(){
    super()
    this.state = {
      response: [],
      users: []
    }
  }

  show(){
    this.setState({show: true})
  }

  close(){
    this.setState({show: false})
  }


  componentDidMount() {
    fetch('http://localhost:9000/api/users/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ response: data })
      this.setState({ users: data.members })
      console.log('full response===>')
      console.log(this.state.response)
      console.log('abstract users data only===>')
      console.log(this.state.users)
    })
    .catch(console.log)
  }
  render() {
    return (
      <div className="container">
      <div className="col-xs-12">
      <h1>All Users</h1>
      {this.state.users.map((user) => (
        <div className="card" key={user.id} onClick={this.show.bind(this)}>
          <div className="card-body">
            <h5 className="card-title">Name: {user.real_name}</h5>
            <h6 className="card-subtitle mb-2 text-muted">Id: {user.id}</h6>
          </div>
        </div>
      ))}

<Modal
      className="test-class" //this will completely overwrite the default css completely
      style={{background: 'red'}} //overwrites the default background
      containerStyle={{background: 'blue'}} //changes styling on the inner content area
      containerClassName="test"
      closeOnOuterClick={true}
      show={this.state.show}
      onClose={this.close.bind(this)}>

      {/* <a style={closeStyle} onClick={this.close.bind(this)}>X</a> */}
      <div>hey</div>

      </Modal>
      </div>
     </div>
    );
  }
}
export default App;
